def ask_madame_spill(question, mood):
    import threading

    def delayed_prints():
        import time
        print("\nğŸŒ€ Madame Spill ÏƒÎºÎ­Ï†Ï„ÎµÏ„Î±Î¹...")
        time.sleep(1.5)
        print("â˜• Î’Î¬Î»Îµ Î­Î½Î±Î½ ÎºÎ±Ï†Î­...")
        time.sleep(1.5)
        print("...Î¯ÏƒÏ‰Ï‚ ÎºÎ±Î¹ Î´ÎµÏÏ„ÎµÏÎ¿.")

    mood_examples = MOOD_EXAMPLES.get(mood, [])
    messages = [
        {"role": "system", "content": SYSTEM_PROMPT},
        *mood_examples,
        {"role": "user", "content": question}
    ]

    # ÎÎµÎºÎ¹Î½Î¬ÎµÎ¹ Î· ÎµÎºÏ„ÏÏ€Ï‰ÏƒÎ· ÏƒÎµ Î¾ÎµÏ‡Ï‰ÏÎ¹ÏƒÏ„ÏŒ thread Î³Î¹Î± Î½Î± Ï„ÏÎ­Ï‡ÎµÎ¹ ÏŒÏƒÎ¿ Ï€ÎµÏÎ¹Î¼Î­Î½Î¿Ï…Î¼Îµ Ï„Î·Î½ Î±Ï€Î¬Î½Ï„Î·ÏƒÎ·
    threading.Thread(target=delayed_prints).start()

    response = openai.ChatCompletion.create(
        model="gpt-4.1",
        messages=messages,
        max_tokens=1000,
        temperature=0.9
    )

    return response['choices'][0]['message']['content']


